mapper.select:
select

from storefront

  
    
      id = #{id,jdbcType=INTEGER}
    
    
      and name = #{name,jdbcType=VARCHAR}
    
    
      and address = #{address,jdbcType=VARCHAR}
    
    
      and img = #{img,jdbcType=VARCHAR}
    
    
      and phone = #{phone,jdbcType=VARCHAR}
    
    
      and status = #{status,jdbcType=INTEGER}
